class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int>diff(nums.size());
        //int maxi=nums[0]-target[0],mini=nums[0]-target[0];
        for(int i=0;i<nums.size();i++){
            diff[i]=nums[i]-target[i];
            //maxi=max(maxi,diff);
            //mini=min(mini,diff);
        }
        long long prev=0,curr=0;
        for(int i=0;i<diff.size();i++){
         curr=diff[i];
         if((prev>0&&curr>0)||(prev<0&&curr<0)){
             long long adjdiff=abs(curr)-abs(prev);
             if(adjdiff>0) ans+=adjdiff;
         }else{
             ans+=abs(curr);
         }
         prev=curr;
        }
        return ans;
    }
};
