class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> a(nums.size());
        int n = nums.size();
        for(int i = 0;i < n;i++) {
            a[i] = (long long) nums[i] - target[i];
        }
        long long sum = abs(a[0]);
        for(int i = 1; i < n; i++){
            if(a[i] != 0) {
                if(a[i] * a[i-1] <= 0){
                    sum += abs(a[i]);
                } else{
                    if(abs(a[i]) <= abs(a[i - 1])) continue;
                    else sum += abs(a[i] - a[i - 1]);
                }
            }
        }
        return sum;
    }
};
