class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int current=0,ans=0;
        bool isIncrement=false;
        for(int i=0;i<nums.size();i++){
            if(nums[i]<=target[i] && !isIncrement){
                isIncrement=true;
                current=0;
            }
            else if(nums[i]>=target[i] && isIncrement){
                isIncrement=false;
                current=0;
            }
            ans=ans+max(abs(nums[i]-target[i])-current,1ll*0);
            current=abs(nums[i]-target[i]);
        }
        return ans;
    }
};
