class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long step = 0;
        vector<int> result;
        for(int i = 0; i < nums.size(); i++) {
            result.push_back(nums[i] - target[i]);
        }

        int index = 0;
        while(index < result.size()) {
            if(result[index] == 0) {
                index++;
            } else {
                if(result[index] > 0) {
                    int nextIndex = index+1;
                    int mainNum = result[index];
                    while(nextIndex < result.size() && result[nextIndex] > 0) {
                        mainNum = min(mainNum, result[nextIndex]);
                        nextIndex++;
                    }

                    step += mainNum;
                    for(int k = index; k < nextIndex; k++) {
                        result[k] -= mainNum;
                    }
                } else {
                    //result[index] < 0
                    int nextIndex = index+1;
                    int mainNum = result[index];
                    while(nextIndex < result.size() && result[nextIndex] < 0) {
                        mainNum = max(mainNum, result[nextIndex]);
                        nextIndex++;
                    }

                    mainNum = abs(mainNum);

                    step += mainNum;
                    for(int k = index; k < nextIndex; k++) {
                        result[k] += mainNum;
                    }
                }
            }
        }

        return step;
    }
};
