class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n = nums.size();

        vector<int> memo;

        for (int i = 0; i < n; ++i) {
            memo.push_back(target[i] - nums[i]);
        }

        long long sol = 0;

        int f = 0;
        long long c = 0;
        long long cc = 0;

        for (int i = 0; i < n; ++i) {
            if (memo[i] == 0) {
                sol += abs(c);
                f = 0;
                c = 0;
            } else if (memo[i] > 0) {
                if (f <= 0) {
                    sol -= c;
                    // sol -= cc;
                    f = 1;
                    c = memo[i];
                    cc = memo[i];
                } else {
                    if (memo[i] > cc) {
                        sol += c - cc;
                        c = memo[i];
                        cc = memo[i];
                    } else {
                        c = max(c, (long long)memo[i]);
                        cc = memo[i];
                    }

                }
            } else {
                if (f >= 0) {
                    sol += c;
                    // sol += cc;
                    f = -1;
                    c = memo[i];
                    cc = memo[i];
                } else {
                    if (memo[i] < cc) {
                        sol -= c - cc;
                        c = memo[i];
                        cc = memo[i];
                    } else {
                        c = min(c, (long long)memo[i]); 
                        cc = memo[i];
                    }

                }
            }
            // cout<<sol<<endl;
        }

        if (c != 0) {
            sol += abs(c);
            // sol += abs(cc);
        }

        return sol;
    }
};
