class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) 
    {
        long long int ans = 0;
        int i = 0, n = a.size();
        vector<vector<int>>mp;
        vector<int>v;
        for(int i = 0 ; i < n ; i++)
        {
            if(a[i] == b[i])
            {
                if(v.size())mp.push_back(v);
                v.clear();
                continue;
            }
            if(v.size() == 0)
            {
                v.push_back(a[i] - b[i]);
                continue;
            }
            int sign = ((a[i] - b[i]) > 0 ? 1 : -1 );
            int esign = (v.back() > 0 ? 1 : -1);
            if(sign == esign)
            {
                v.push_back(a[i] - b[i]);
            }
            else
            {
                mp.push_back(v);
                v.clear();
                v.push_back(a[i] - b[i]);
            }
        }
        if(v.size())mp.push_back(v);
        for(auto &x:mp)
        {
            for(auto &e : x) e = abs(e);
            long long int res = x[0];
            for(int i = 1 ; i < x.size() ; i++)
            {
                res += max(0,x[i] - x[i - 1]);
            }
            ans += res;
        }
        return ans;        
    }
};
