class Solution {
	public long minimumOperations(int[] nums, int[] target) {
		int n = nums.length;
		int[] nb = new int[n+1];
		for(int i = 0;i < n;i++){
			nb[i] = nums[i] - (i == 0 ? 0 : nums[i-1]);
		}
		nb[n] = -nums[n-1];
		int[] tt = new int[n+1];
		for(int i = 0;i < n;i++){
			tt[i] = target[i] - (i == 0 ? 0 : target[i-1]);
		}
		tt[n] = -target[n-1];

		long ans = 0;
		for(int i = 0;i <= n;i++){
			if(nb[i] < tt[i]){
				ans += tt[i] - nb[i];
			}
		}
		return ans;
	}
}
