#include <bits/stdc++.h>
using namespace std;

#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target)
    {

        vector<ll> v;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            v.push_back(nums[i] - target[i]);
        }
        ll op=0;
        for(int i=0;i<n;i++)
        {
            if(i==0)
            {
                op+=abs(v[i]);
                continue;
            }
            if(v[i]==0)
                continue;
            if(v[i]*v[i-1]<=0)
            {
                op+=abs(v[i]);
                continue;
            }
            if(v[i]<0)
            {
                if(v[i]<v[i-1])
                    op+=abs(v[i]-v[i-1]);
            }
            else
            {
                if(v[i]>v[i-1])
                    op+=abs(v[i]-v[i-1]);
            }
        }
        return op;
    }
};
