class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> cnt(n , 0) ,sum(n , 0);
        for(int i = 0; i < n; i++) cnt[i] = nums[i] - target[i];
        sum[0] = cnt[0];
        for(int i = 1; i < n; i++) sum[i] = cnt[i] - cnt[i-1];
        long long x = 0 , y = 0;
        for(int i = 0; i < n; i++) {
            if(sum[i] < 0) y -= sum[i];
            else x += sum[i];
        }
        return max(x , y);
    }
};
