class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0] * n
        for i in range(n):
            diff[i] = target[i] - nums[i]

        cur_cost = 0
        res = 0
        for i in range(n):
            if i == 0:
                cur_cost = abs(diff[i])
            else:
                if diff[i] > 0:
                    if diff[i-1] > 0:
                        if diff[i] > diff[i-1]:
                            cur_cost += diff[i] - diff[i-1]
                    else:
                        res += cur_cost
                        cur_cost = abs(diff[i])
                elif diff[i] < 0:
                    if diff[i-1] < 0:
                        if diff[i] < diff[i-1]:
                            cur_cost += abs(diff[i] - diff[i-1])
                    else:
                        res += cur_cost
                        cur_cost = abs(diff[i])
                else:
                    res += cur_cost
                    cur_cost = 0
            # print(i, cur_cost)

        res += abs(cur_cost)
        return res
