class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = target.size();
        long long ans = abs(target[0] - nums[0]);
        for (int i = 1; i < n; ++i) {
            if(target[i]>nums[i]) {
                if(target[i-1]>nums[i-1]) { // 如果和前一个趋势一样，则借用
                    ans += max((target[i]-nums[i])-(target[i-1]-nums[i-1]), 0);
                }else{
                    ans += target[i]-nums[i];
                }
            }else if(target[i]<nums[i]) {
                if(target[i-1]<nums[i-1]) { // 如果和前一个趋势一样，则借用
                    ans += max((nums[i]-target[i])-(nums[i-1]-target[i-1]), 0);
                }else{
                    ans += nums[i]-target[i];
                }
            }

        }
        return ans;

    }
};
