class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long> dif;
        int n=nums.size();
        for(int i=0;i<n;i++){
            dif.push_back(nums[i]-target[i]);
        }
        long long ans=0,f=0;
        for(int i=0;i<n;i++){
            if(f==0){
                ans+=abs(dif[i]);
                if(dif[i]<0) f=1;
                else f=2;
            }
            else if(f==1){
                if(dif[i]<0){
                    if(dif[i-1]>dif[i]){
                        ans+=abs(dif[i-1]-dif[i]);
                    }else  {}
                }
                else{
                    f=2;
                    ans+=abs(dif[i]);
                }
            }
            else{
                if(dif[i]>0){
                    if(dif[i]<dif[i-1]){}
                    else{
                        ans+=(dif[i]-dif[i-1]);
                    }
                }
                else{
                    f=1;
                    ans+=abs(dif[i]);
                }
            }

        }
        return ans;

    }
};
