class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        s = [target[i] - nums[i] for i in range(len(nums))]
        sub = [s[0]]
        for i in range(1, n):
            sub.append(s[i] - s[i - 1])
        pos,neg = 0,0
        for i in range(n):
            if sub[i] > 0:
                pos += sub[i]
            else:
                neg += sub[i]
        return max(pos,-neg)
