class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> a(n);

        for (int i = 0; i < n; ++i) {
            a[i] = target[i] - nums[i];
        }
        long long q=0,p=0,c=0;
        for(int i=1;i<n;i++)
            {
                c=a[i]-a[i-1];
                // cout<<c<<" ";
                if(c>0)
                {
                    p+=c;
                }
                else 
                q-=c;
            }
            c=a[0];
            if(c>0)
                {
                    p+=c;
                }
                else 
                q-=c;
            long long ans1=max(p,q);
            // puts("");
            // for(auto i:a)cout<<i<<" ";
            return ans1;
    }
};
