class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int length = nums.size();
        for(int i=0;i<length;i++){
            nums[i] -= target[i];
            // cout << nums[i] << " ";
        }
        // cout << endl;
        long long ret = abs(nums[0]);
        for(int i=1;i<length;i++){
            // printf("%d %d\n", nums[i], nums[i-1]);
            int nf = (nums[i]>0)-(nums[i]<0);
            if(nf*nums[i-1]>=0){
                if(abs(nums[i])>abs(nums[i-1])){
                    ret += abs(nums[i]-nums[i-1]);
                }
            }else{
                ret += abs(nums[i]);
            }
            // cout<<ret<<endl;
        }
        return ret;
    }
};
