class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++) v[i]=target[i]-nums[i];

        for(int i=0;i<n;i++){
            if(v[i]==0) continue;
            if(v[i]>0){
                int mini=INT_MAX;
                int start=i;
                while(i<n && v[i]>0) mini=min(mini,v[i]),i++;
                ans+=mini;
                for(int j=start;j<i;j++) v[j]-=mini;
                i=start-1;
            }
            else if(v[i]<0){
                int mini=INT_MAX;
                int start=i;
                while(i<n && v[i]<0) mini=min(mini,abs(v[i])),i++;
                ans+=mini;
                for(int j=start;j<i;j++) v[j]+=mini;
                i=start-1;
            }
        }
        return ans;
    }
};
