class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [a - b for a, b in zip(nums, target)]
        def add(arr):
            if len(arr) == 0:  
                return 0
            res = abs(arr[0])
            for i in range(1, len(arr)):
                if abs(arr[i]) > abs(arr[i-1]):
                    res += abs(arr[i]) - abs(arr[i-1])
            return res
        diff.append(0)
        res = 0
        l = 0
        positive = None
        for r in range(len(diff)):
            if positive is None:
                if diff[r] > 0:
                    positive = True
                    l = r
                elif diff[r] < 0:
                    positive = False
                    l = r

            elif positive is True and diff[r] <= 0:
                res += add(diff[l:r])
                if diff[r] < 0:
                    positive = False
                    l = r
                else:
                    positive = None
            elif positive is False and diff[r] >= 0:
                res += add(diff[l:r])
                if diff[r] > 0:
                    positive = True
                    l = r
                else:
                    positive = None

            # print(positive, l, r, res)

        return res
