class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums.append(0)
        target.append(0)
        diff = [0]
        times = 0
        n = len(nums)
        for pnt in range(n):
            temp = target[pnt] - nums[pnt]
            if temp * diff[-1] <= 0:
                cur = 0
            else:
                cur = temp
            while diff and abs(diff[-1]) > abs(cur):
                if abs(diff[-2]) >= abs(cur):
                    times += abs(diff[-1] - diff[-2])
                else:
                    times += abs(diff[-1] - cur)
                diff.pop()
            if temp != diff[-1]:
                diff.append(temp)
        return times
