# class Solution:
#     def minimumOperations(self, nums: List[int], target: List[int]) -> int:

from typing import List

class Solution:
    def solve(self, a: List[int]) -> int:
        n = len(a)
        if a[0] < 0:
            a = [-x for x in a]
        answer = a[0]
        for i in range(1, n):
            if a[i] > a[i - 1]:
                answer += a[i] - a[i - 1]
        return answer

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        v = []
        def ok(a, b):
            if a < 0:
                return b < 0
            if a > 0:
                return b > 0
            return b == 0

        for i in range(n):
            diff = target[i] - nums[i]
            if v and ok(v[-1][-1], diff):
                v[-1].append(diff)
            else:
                v.append([diff])

        answer = 0
        for a in v:
            answer += self.solve(a)
        return answer
