class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans=0;
        long long int op=0;
        int prev=0;
        for(int i=0;i<nums.size();i++){
            if(prev==0){
                ans+=abs(target[i]-nums[i]);
                op=abs(target[i]-nums[i]);
                if(target[i]-nums[i]>0)prev=1;
                else if(target[i]-nums[i]<0) prev=-1;
                else prev=0;
            }
            else if(target[i]-nums[i]>0&&prev>0){
                if(target[i]-nums[i]>op){
                    ans+=target[i]-nums[i]-op;
                    op=target[i]-nums[i];
                }
                else if(target[i]-nums[i]<op){
                    op=target[i]-nums[i];
                }
            }
            else if(target[i]-nums[i]<0&&prev<0){
                if(abs(target[i]-nums[i])>op){
                    ans+=abs(target[i]-nums[i])-op;
                    op=abs(target[i]-nums[i]);
                }
                else if(abs(target[i]-nums[i])<op){
                    op=abs(target[i]-nums[i]);
                }
            }
            else if(target[i]-nums[i]>0&&prev<0){
                ans+=target[i]-nums[i];
                op=target[i]-nums[i];
                prev=1;
            }
            else if(target[i]-nums[i]<0&&prev>0){
                ans+=abs(target[i]-nums[i]);
                op=abs(target[i]-nums[i]);
                prev=-1;
            }
            else{
                prev=0;
            }
        }

        return ans;
    }
};
