class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();

        vector<int>diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        int inc=0;
       long long ans=0;
        int dec=0;
        for(int i=0;i<n;i++){
            if(diff[i]==0){
                inc=0;
                dec=0;
            }
            else if(diff[i]>0){
                dec=0;
                if(inc>diff[i]){
                    inc=diff[i];
                }
                else{
                    ans+=diff[i]-inc;
                    inc=diff[i];
                }
            }
            else{
                inc=0;
                int x=-diff[i];
                if(dec>x){
                    dec=x;
                }
                else{
                    ans+=x-dec;
                    dec=x;
                }
            }
        }
        return ans;

    }
};
