class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        long long maxi=0,mini=0,n=nums.size();
        vector<long long> v;
        for(int i=0;i<nums.size();i++){
            v.push_back(nums[i]-target[i]);
        }
        for(int i=0;i<nums.size();i++){
            cout<<v[i]<<" ";
        }cout<<endl;
        ans+=abs(v[0]);
        for(int i=1;i<n;i++){
            if(v[i]<0&&v[i-1]<0){
                if(v[i]<v[i-1])ans+=abs(v[i]-v[i-1]);
            }else if(v[i]<0){
                ans+=abs(v[i]);
            }
            if(v[i]>0&&v[i-1]>0){
                if(v[i]>v[i-1])ans+=v[i]-v[i-1];
            }else if(v[i]>0){
                ans+=v[i];
            }
        }
        return ans;
    }
};
