class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        r=0
        f=0
        n=len(nums)
        l=[]
        m=0
        r=0
        for i in range(n):
            if(nums[i]>target[i]):
                if(f):
                    r+=m
                    m=0
                f=0
                if(m<nums[i]-target[i]):
                    l.append(nums[i]-target[i]-m)
                else:
                    l.append(0)
                m=nums[i]-target[i]
            else:
                if(not f):
                    r+=m
                    m=0
                f=1
                if(m<target[i]-nums[i]):
                    l.append(target[i]-nums[i]-m)
                else:
                    l.append(0)
                m=target[i]-nums[i]
        # print(l)
        return sum(l)
