class Solution(object):
    def minimumOperations(self, nums, target):
        """
        :type nums: List[int]
        :type target: List[int]
        :rtype: int
        """
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]

        ans = abs(diff[0])
        for i in range(1,n):
            if diff[i-1] >= 0 and diff[i] >= 0:
                ans += max(diff[i] - diff[i - 1], 0)

            elif diff[i-1] <= 0 and diff[i] <= 0:
                ans += max(abs(diff[i]) - abs(diff[i - 1]), 0)

            else:
                ans += abs(diff[i])

        return ans
