class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        for(int i=0;i<nums.size();i++){diff.push_back(target[i]-nums[i]);}
        vector<vector<int>> pos,neg;
        vector<int> l;
        for(int i=0;i<diff.size();i++){
            if(l.size()==0){l.push_back(diff[i]);}
            else if(diff[i]>=0 && l.back()<0){
                neg.push_back(l);
                l={diff[i]};
            }
            else if(diff[i]<0 && l.back()>=0){
                pos.push_back(l);
                l={diff[i]};
            }
            else{l.push_back(diff[i]);}
        }
        if(l.size()!=0){
            if(l.back()>=0){pos.push_back(l);}
            else{neg.push_back(l);}
        }


        long long steps=0;
        for(long long i=0;i<pos.size();i++){
            long long mini=pos[i][0];
            steps+=pos[i][0];
            for(long long j=1;j<pos[i].size();j++){
                if(pos[i][j]<mini){mini=pos[i][j];}
                else if(pos[i][j]>mini){
                    steps+=(pos[i][j]-mini);
                    mini=pos[i][j];
                }
            }
        }
        for(long long i=0;i<neg.size();i++){
            long long mini=abs(neg[i][0]);
            steps+=mini;
            for(long long j=1;j<neg[i].size();j++){
                if(abs(neg[i][j])<mini){mini=abs(neg[i][j]);}
                else if(abs(neg[i][j])>mini){
                    steps+=(abs(neg[i][j])-mini);
                    mini=abs(neg[i][j]);
                }
            }
        }
        return steps;
    }
};
