class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff=[target[i]-nums[i] for i in range(len(nums))]       
        res=0
        cur=0       
        for i in range(len(diff)):
            if i == 0 or (diff[i-1]>0 and diff[i]>0) or (diff[i-1]<0 and diff[i]<0):
                adp=abs(diff[i])-abs(cur)
                if adp> 0:
                    res+=adp
                cur=diff[i]
            else:  
                res+=abs(diff[i])
                cur= diff[i]        
        return res
