class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffbw=[]
        for i in range(len(nums)):
            diffbw.append(target[i] - nums[i])
        yans,temp_diff,i = 0,0,0
        while(i<len(diffbw)):
            if i == 0 or (diffbw[i - 1] > 0 and diffbw[i] > 0) or (diffbw[i - 1] < 0 and diffbw[i] < 0):
                rop = abs(diffbw[i]) - abs(temp_diff)
                if rop > 0:
                    yans += rop
                temp_diff = diffbw[i]
            else:  
                yans += abs(diffbw[i])
                temp_diff = diffbw[i]
            i+=1     
        return yans
