#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<ll> diff;
        for (int i=0;i<nums.size();i++){
            diff.push_back(target[i] - nums[i]);
        }
        ll i = 0;
        ll maxm=INT_MIN;
        ll minm=INT_MAX;
        ll ans = 0;
        ll j = i;
        while (i<diff.size()) {
            int flag = 0;
            j = i;
            while (j<diff.size() && diff[j] > 0){
                minm = min(minm,diff[j]);
                j++;
                flag = 1;
            }
            if (flag == 1) {
                ans += minm;
                for (int k = i; k<j ;k++) {
                    diff[k]-=minm;
                }
            }
            int s = j;
            while (j<diff.size() && diff[j] < 0){
                maxm = max(maxm,diff[j]);
                j++;
                flag = 2;
            }

            if (flag == 2) {
                ans += abs(maxm);
                for (int k = s; k<j ;k++) {
                    diff[k]+=abs(maxm);
                }
            }

            maxm=INT_MIN;
            minm=INT_MAX;
            if (flag == 0) i++;

        }
        return ans;
    }
};
