class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long res = 0;
		int n = nums.length;
		int[] d = new int[n];
		Arrays.setAll(d, idxx -> target[idxx] - nums[idxx]);
		int lc = 0;
		for (var x : d) {
			if (x == 0) {
				lc = 0;
			} else if (x > 0) {
				if (lc >= 0) {
					if (lc < x)
						res += x - lc;
					lc = x;
				} else {
					res += x;
					lc = x;
				}
			} else {
				if (lc <= 0) {
					if (lc > x)
						res += lc - x;
					lc = x;
				} else {
					res += -x;
					lc = x;
				}
			}
		}

		return res;
    }
}
