from typing import List

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]
        t_o = 0
        c_diff = 0

        for i in range(len(diff)):
            if i == 0 or (diff[i - 1] > 0 and diff[i] > 0) or (diff[i - 1] < 0 and diff[i] < 0):
                # If we are at the start or the diffs are consistently pos or neg
                add_op = abs(diff[i]) - abs(c_diff)
                if add_op > 0:
                    t_o += add_op
                c_diff = diff[i]
            else:
                t_o += abs(diff[i])
                c_diff = diff[i]

        return t_o
