class Solution {
public:
#define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i =0;i<n;i++){
            nums[i] = target[i]-nums[i];
        }
        nums.insert(nums.begin(),0);
        for(int i = n;i>=1;i--)nums[i] -= nums[i-1];
        ll pos = 0,neg = 0;
        for(auto &i:nums){
            if(i>=0)pos += i;
            else neg += -i;
        }
        return max(pos,neg);
    }
};
