class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long>res(n);
        for(int i=0;i<n;i++){
            res[i]=nums[i]-target[i];
        }
        long long p,d;
        if(res[0]<0){
            p=-1;
            d=res[0];
        } else {
            p=1;
            d=res[0];
        }
        long long ans=0;
        for(int i=1;i<n;i++){
            if(res[i]<0&&p==-1){
                if(res[i]>d){
                    ans+=abs(d-res[i]);
                    d=res[i];
                } else {
                    d=min(d,res[i]);
                }
            }else if(res[i]<0&&p==1){
                ans+=1ll * abs(d);
                p=-1;
                d=res[i];
            } else if(res[i]>=0&&p==-1){
                ans+=1ll * abs(d);
                p=1;
                d=res[i];
            }else {
                if(res[i]<d){
                    ans+=abs(d-res[i]);
                    d=res[i];
                } else {
                    d=max(d,res[i]);
                }
            }
        }
        ans+=1ll*abs(d);
        return ans;
    }
};
