class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> ans;
        for(int i=0;i<nums.size();i++){
            ans.push_back(target[i]-nums[i]);
        }

        long long cnt=0;
        int prev=0;
        int maxi=INT_MIN;
        bool posi=true;
        bool flag=true;
        for(int i=0;i<nums.size();i++){
            if(ans[i]>0){
                if(maxi==INT_MIN || !posi || flag){
                    prev=abs(ans[i]);
                    cnt+=prev;
                    maxi=max(maxi,ans[i]);
                    posi=true;
                    flag=false;
                }
                else{
                    int num=ans[i];
                    if(num>=prev){
                        cnt+=(num-prev);
                        prev=num;
                    }
                    else{
                        prev=num;
                    }
                }

            }
            else if(ans[i]<0){
                if(maxi==INT_MIN || posi || flag){
                    prev=abs(ans[i]);
                    cnt+=prev;
                    maxi=-1*ans[i];
                    posi=false;
                    flag=false;
                }
                else{
                    int num=abs(ans[i]);
                    if(num>=prev){
                        cnt+=(num-prev);
                        prev=num;
                    }
                    else{
                        prev=abs(num);
                    }
                }
            }
            else {
                maxi=INT_MIN;
                flag=true;
                prev=0;
            }
        }
        return cnt;
    }
};
