class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int arr[] = new int[nums.length];

        for(int i = 0; i < nums.length; i++) {
            arr[i] = target[i]-nums[i];
            // System.out.print(arr[i] + " ");
        }
        // System.out.println();

        int before = 0;
        long result = 0;
        int idx = 0;
        while(idx < nums.length) {
            if ( before == 1 ) {
                int remain = 0;
                while(idx < nums.length && arr[idx] > 0 ) {
                    if ( remain > Math.abs(arr[idx])) remain = Math.abs(arr[idx]);
                    else {
                        result += Math.abs(arr[idx]) - remain;
                        remain = Math.abs(arr[idx]);
                    }
                    // System.out.println(result + " " + arr[idx]);
                    idx++;
                }
            } else if ( before == -1 ){
                int remain = 0;
                while(idx < nums.length && arr[idx] < 0 ) {
                    if ( remain > Math.abs(arr[idx])) remain = Math.abs(arr[idx]);
                    else {
                        result += Math.abs(arr[idx]) - remain;
                        remain = Math.abs(arr[idx]);
                    }
                    idx++;
                }
            } else{
                if ( arr[idx] > 0 ) {
                    int remain = 0;
                    while(idx < nums.length && arr[idx] > 0 ) {
                        if ( remain > Math.abs(arr[idx])) remain = Math.abs(arr[idx]);
                        else {
                            result += Math.abs(arr[idx]) - remain;
                            remain = Math.abs(arr[idx]);
                        }
                        idx++;
                    }                                
                } else if ( arr[idx] < 0 ) {
                    int remain = 0;
                    while(idx < nums.length && arr[idx] < 0 ) {
                        if ( remain > Math.abs(arr[idx])) remain = Math.abs(arr[idx]);
                        else {
                            result += Math.abs(arr[idx]) - remain;
                            remain = Math.abs(arr[idx]);
                        }
                        idx++;
                    }                                
                } else{
                    idx++;
                }
            }

            if ( idx < nums.length ) {
                if ( arr[idx] == 0 ) before = 0;
                else if ( arr[idx] < 0 ) before = -1;
                else before = 1;
            }
            // System.out.println(result);
        }

        return result;
    }
}
