class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long>nums2(n);
        for(int i=0;i<n;i++){
            nums2[i]=1LL*nums[i]-target[i];
        }
        long long sum=abs(nums2[0]);
        for(int i=1;i<n;i++){
            if(nums2[i]==0)continue;
            if(nums2[i]*nums2[i-1]>0){
                if(abs(nums2[i])<=abs(nums2[i-1]))continue;
                else sum+=abs(nums2[i]-nums2[i-1]);
            }else sum+=abs(nums2[i]);
        }
        return sum;
    }
};
