class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans=0;
        long[] arr=new long[nums.length+1];
        for (int i = 0; i < arr.length-1; i++) {
            arr[i]=target[i]-nums[i];
        }
        for (int i = 0; i < arr.length-1; i++) {
            if(arr[i]*arr[i+1]<0){
                ans+=Math.abs(arr[i]);
            }else {
                ans+=Math.abs(arr[i])-Long.min(Math.abs(arr[i]),Math.abs(arr[i+1]));
            }
        }
        return ans;
    }
}
