class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long x = 0;

        for (int i = 0; i < nums.size(); i++) {
            nums[i] = target[i] - nums[i];
            if (i != 0) {
                if (nums[i] >= 0 && nums[i - 1] >= 0) {
                    if (nums[i - 1] < nums[i]) {
                        x += nums[i] - nums[i - 1];
                    }
                } else if (nums[i] < 0 && nums[i - 1] < 0) {
                    if (nums[i] < nums[i - 1]) {
                        x += nums[i - 1] - nums[i];
                    }
                }

                else {
                    x += abs(nums[i]);
                }
            } else {
                x += abs(nums[i]);
            }
        }
        return x;
    }
};
