#define ll long long

class Solution {
public:
    ll get_ans(vector<int>& nums, vector<int>& target, int s, int e){
        if(e - s <= 0)
            return 0;

        ll ans = 0;
        int tg[e - s + 1];

        for(int i = s; i < e; i++)
            tg[i - s] = abs(target[i] - nums[i]);

        ll total = tg[0];
        for (int i = 1; i < e - s; ++i) {
            if (tg[i] > tg[i-1]) {
                total += tg[i] - tg[i-1];
            }
        }

        return total;
    }

    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ix = 0, ans = 0, par = nums[0] - target[0];

        for(int i = 0; i < nums.size(); i++){
            if((par >= 0 && nums[i] - target[i] >= 0) || (par <= 0 && nums[i] - target[i] <= 0))
                par = nums[i] - target[i];
            else
                ans += get_ans(nums, target, ix, i), ix = i, par = nums[i] - target[i];
        } 

        ans += get_ans(nums, target, ix, nums.size());
        return ans;
    }
};
