typedef long long LL;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<LL> arr(n);
        for(int i = 0; i < n; i++) {
            arr[i] = target[i] - nums[i];
        }
        LL ans = 0, last = 0;
        for(int i = 0; i < n; i++) {
            if(arr[i]*last <= 0) {
                ans += abs(arr[i]);
            }else {
                ans += max(0LL, abs(arr[i]) - abs(last));
            }
            last = arr[i];
        }
        return ans;
    }
};
