class Solution {
public:
    long long solve(vector<int>a){
        long long ans=0;
        int n=a.size();
        for(int i=1;i<n;i++){
            ans-=min(a[i-1],a[i]);
        }
        for(auto i:a){
            ans+=i;
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int>t;
        int n=nums.size();
        vector<int>a;
        for(int i=0;i<n;i++){
            a.push_back(nums[i]-target[i]);
        }
        for(int i=0;i<n;){
            if(a[i]<0){
                while(i<n&&a[i]<0){
                    t.push_back(abs(a[i]));
                    i++;
                }
                ans+=solve(t);
                t.clear();
            }
            else if(a[i]>0){
                while(i<n&&a[i]>0){
                    t.push_back(abs(a[i]));
                    i++;
                }
                ans+=solve(t);
                t.clear();
            }else{
                i++;
            }
        }
        return ans;
    }
};
