class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long int ans = 0;
        for(int  i = 0; i < n; i++)
        {
            long long int m = 0;
            long long int d = nums[i] - target[i];
            if(d >= 0)
            {
                int j = i;
                while(j < n && d >= 0)
                {

                    d = nums[j] - target[j];
                    if(d < 0) break;
                    if(d < m) {ans+=m-d;}
                    m = d;
                    // m = max(m, d);
                    j++;
                }
                i = j-1;
            }
            else if(d < 0)
            {
                int j = i;
                while(j < n && d < 0)
                {
                    d = nums[j] - target[j];
                    if(d > 0) break;
                    if(d > m) {ans+=abs(m-d);}
                    m = d;
                    j++;
                }

                i = j-1;
            }

            ans+=abs(m);
        }
        return ans;
    }
};
