class Solution {
public:
    long long solve( int a, int b, vector<int> &t ){
        long long ans = 0; vector<int> st;
        st.push_back(t[a]);
        for (int i = a+1; i<=b; i++) {
            if (t[i] > st.back()) {
                st.push_back(t[i]);
                continue;
            } if (t[i] == st.back()) continue;
            int tp = st.back();
            while (!st.empty() && t[i] < st.back()) st.pop_back();
            ans += tp - t[i];
            st.push_back(t[i]);
        } if (!st.empty()) ans += st.back();
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ios_base::sync_with_stdio(false); cin.tie(NULL); cout.tie(NULL);
        long long n = nums.size(), ans = 0, l = 0; vector<int> d; vector<pair<int,int>> a;
        for ( int i = 0; i<n; i++ ) d.push_back(target[i]-nums[i]);
        for ( int i = 1; i<n; i++ ){
            if ( ( d[i-1] >= 0 && d[i] < 0 ) || ( d[i-1] < 0 && d[i] >= 0 ) ) {
                a.push_back({l,i-1});
                l = i;
            }
        } a.push_back({l,n-1});
        for ( int i = 0; i<n; i++ ) if ( d[i] < 0 ) d[i] *= -1;
        for ( auto i : a ) ans += solve(i.first,i.second,d);
        return ans;
    }
};
