class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        stack<long long> stk;
        stk.push(0);
        int flag = 0;
        long long ans = 0;
        for (int i = 0; i <= n; i++) {
            long long num;
            if (i == n) {
                num = 0;
            } else {
                num = nums[i] - target[i];
            }

            long long t = stk.top();
            int tmpflag;
            if (num > t) {
                tmpflag = 1;
            } else if (num < t) {
                tmpflag = -1;
            } else {
                continue;
            }
            // cout << flag << " " << t << endl;
            if (tmpflag * flag > 0) {
                // stk.pop();
            } else if (tmpflag * flag < 0) {
                ans += 1LL * flag * t;
                // cout << flag << " " << t << endl;
            }
            stk.push(num);
            flag = tmpflag;
        }
        return ans;
    }
};
