class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        vector<long long> difference_vect;

        for (long long i = 0; i < nums.size(); i++){
            difference_vect.push_back(target[i] - nums[i]);
        }

        long long answer = 0;
        long long current_diff = 0;

        for (long long i = 0; i < difference_vect.size(); i++){
            if (i == 0 || (difference_vect[i - 1] < 0 && difference_vect[i] < 0) || (difference_vect[i - 1] > 0 && difference_vect[i] > 0)){

                long long extra = abs(difference_vect[i]) - abs(current_diff);

                if (extra > 0){
                    answer = answer + extra;
                }
                current_diff = difference_vect[i];

            }
            else{

                answer = answer + abs(difference_vect[i]);
                current_diff = difference_vect[i];

            }
        }
        return answer;
    }
};
