class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int carry = 0;
        long ans = 0;
        for (int i = 0; i < n; ++i) {
            int diff = nums[i]-target[i];
            if ((carry>0&&diff<0)||(carry<0&&diff>0)) {
                ans += Math.abs(diff);
            } else {
                if (Math.abs(diff)>Math.abs(carry))
                    ans += Math.abs(diff)-Math.abs(carry);
            }
            carry = diff;
            //System.out.println(diff+" "+ans);
        }
        return ans;
    }
}
