class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        vector<int> t(nums.begin(), nums.end());
        int len = nums.size();
        for(int i = 0; i < len; ++i)
            t[i] = target[i]-nums[i];
        vector<int> dp(len, 0);
        dp[0] = t[0];
        for(int i = 1; i < len; ++i)
            dp[i] = t[i]-t[i-1];
        long long zs = 0;
        long long fs = 0;
        for(int i = 0; i < len; ++i)
            if(dp[i] > 0)
                zs += dp[i];
            else if(dp[i] < 0)
                fs += dp[i];
        return max(zs, abs(fs));
    }
};
