#include <iostream>
#include <vector>
#include <cmath>
using namespace std;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> differences(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            differences[i] = static_cast<long long>(target[i]) - nums[i];
        }

        long long totalOperations = 0;
        long long currentDiff = 0;

        for (int i = 0; i < differences.size(); i++) {
            if (i == 0 || (differences[i - 1] > 0 && differences[i] > 0) || 
                          (differences[i - 1] < 0 && differences[i] < 0)) {
                long long additionalOps = abs(differences[i]) - abs(currentDiff);
                if (additionalOps > 0) {
                    totalOperations += additionalOps;
                }
                currentDiff = differences[i];
            } else {
                totalOperations += abs(differences[i]);
                currentDiff = differences[i];
            }
        }

        return totalOperations;
    }
};
