package main

import (
	"math"
)

func minimumOperations(nums []int, target []int) int64 {
	n := len(nums)
	diff := make([]int, n)
	for i := 0; i < n; i++ {
		diff[i] = target[i] - nums[i]
	}

	totalOperations := int64(0)
	currentSegmentDiff := int64(0)

	for i := 0; i < n; i++ {
		diffI := int64(diff[i])
		if i == 0 || (currentSegmentDiff > 0 && diffI > 0) || (currentSegmentDiff < 0 && diffI < 0) {
			additionalOperations := int64(math.Abs(float64(diffI))) - int64(math.Abs(float64(currentSegmentDiff)))
			if additionalOperations > 0 {
				totalOperations += additionalOperations
			}
			currentSegmentDiff = diffI
		} else {
			totalOperations += int64(math.Abs(float64(diffI)))
			currentSegmentDiff = diffI
		}
	}
	return totalOperations
}
