class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
    int n = nums.size();
        vector<int> diff(n);
        long long totalOpe = 0;
        int currSegmentDiff = 0;


        for (int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }


        for (int i = 0; i < n; i++) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int additionalOpe = abs(diff[i]) - abs(currSegmentDiff);
                if (additionalOpe > 0) {
                    totalOpe += additionalOpe;
                }
                currSegmentDiff = diff[i];
            } else {
                totalOpe += abs(diff[i]);
                currSegmentDiff = diff[i];
            }
        }

        return totalOpe;
    }
};
