class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int> diff;
        for(int i=0;i<nums.size();i++){
            diff.push_back(target[i]-nums[i]);
        }
        // int i=0;
        // while(i<diff.size()){
        //     if(diff[i]==0) i++;
        //     else if(diff[i]>0){
        //         int maxi=0;
        //         while(i<diff.size() && diff[i]>0 ){
        //             maxi=max(maxi,diff[i]);
        //             i++;
        //         }
        //         ans+=maxi;
        //     }
        //     else{
        //         int maxi=0;
        //         while(i<diff.size() && diff[i]<0 ){
        //             maxi=min(maxi,diff[i]);
        //             i++;
        //         }
        //         ans-=maxi;
        //     }
        // }
        // return ans;

        long long cnt=0,value=0;
        for (int i=0;i<diff.size();i++){
            if(i==0 || (diff[i-1]>0 && diff[i]>0) || diff[i-1]<0 && diff[i]<0){
                long long k=abs(diff[i])-abs(cnt);
                if(k>0) value+=k;
                cnt=value;
            }
            else value+=abs(diff[i]);
            cnt=diff[i];

        }

        return value;
    }
};
